export default function Leaderboard(){



        const  matches = [
            {
                "matchDate": 1651744228685,
                "stadium": "Maracanã",
                "homeTeam": "Brazil",
                "awayTeam": "Serbia",
                "matchPlayed": true,
                "homeTeamScore": 1,
                "awayTeamScore": 0
            },
            {
                "matchDate": 1651744228685,
                "stadium": "Stade de Suisse",
                "homeTeam": "Switzerland",
                "awayTeam": "Serbia",
                "matchPlayed": true,
                "homeTeamScore": 2,
                "awayTeamScore": 2
            },
            {
                "matchDate": 1651744228685,
                "stadium": "Stadion Rajko Mitic",
                "homeTeam": "Serbia",
                "awayTeam": "Cameroon",
                "matchPlayed": true,
                "homeTeamScore": 0,
                "awayTeamScore": 1
            },
            {
                "matchDate": 1651744228685,
                "stadium": "Maracanã",
                "homeTeam": "Brazil",
                "awayTeam": "Switzerland",
                "matchPlayed": true,
                "homeTeamScore": 3,
                "awayTeamScore": 0
            },
            {
                "matchDate": 1651744228685,
                "stadium": "Maracanã",
                "homeTeam": "Brazil",
                "awayTeam": "Cameroon",
                "matchPlayed": true,
                "homeTeamScore": 4,
                "awayTeamScore": 4
            },
            {
                "matchDate": 1651744228685,
                "stadium": "Stade de Suisse",
                "homeTeam": "Switzerland",
                "awayTeam": "Cameroon",
                "matchPlayed": true,
                "homeTeamScore": 2,
                "awayTeamScore": 2
            }
        ];

            const renderTableData = (items) => {

                console.log(items);
                return (
                    items.map((item) =>
                        <tr key={item.id} className={item.selected ? "selected" : ""}>

                            <td>

                                {item.homeTeam === "Brazil" && <img src="https://countryflagsapi.com/png/br" alt="Brazil flag" width="53px" height="37px" />}
                                {item.homeTeam === "Serbia" &&  <img src="https://countryflagsapi.com/png/rs" alt="Serbia flag" width="53px" height="37px"/>}
                                {item.homeTeam === "Switzerland" &&  <img src="https://countryflagsapi.com/png/ch" alt="Switzerland flag" width="53px" height="37px"  />}
                                {item.homeTeam === "Cameroon" && <img src="https://countryflagsapi.com/png/cm" alt="Cameroon flag" />}
                                <p> {item.homeTeam} </p>
                             </td>

                            <td>
                            {item.awayTeam === "Brazil" && <img src="https://countryflagsapi.com/png/br" alt="Brazil flag" width="53px" height="37px" />}
                                {item.awayTeam === "Serbia" &&  <img src="https://countryflagsapi.com/png/rs" alt="Serbia flag" width="53px" height="37px"/>}
                                {item.awayTeam === "Switzerland" &&  <img src="https://countryflagsapi.com/png/ch" alt="Switzerland flag" width="53px" height="37px"  />}
                                {item.awayTeam === "Cameroon" && <img src="https://countryflagsapi.com/png/cm" alt="Cameroon flag" width="53px" height="37px" />}

                                {
                                     <p> {item.awayTeam} </p>
                                }
                            </td>
                            <td>
                                {   item.MP    }
                            </td>
                            <td>
                            {   item.GF    }
                            </td>
                            <td>
                            {   item.GA    }
                            </td>
                            <td>
                            {   item.GD    }
                            </td>
                            <td>
                            {   item.Points   }
                            </td>
                        </tr>
                    )
                )
            }

            return (
                <div className="m-4">
                <h3 className="title">League Standings</h3>
                <div className="table-responsive">
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                <th scope="col">Team Name</th>
                                <th scope="col">MP</th>
                                <th scope="col">GF</th>
                                <th scope="col">GA</th>
                                <th scope="col">Points</th>
                            </tr>
                        </thead>
                        <tbody>
                            {renderTableData(matches)}                    
                        </tbody>

                    </table>

                </div>
            </div>

              );
}
