export default function Schedule(){


    const renderTableData = (items, idArray) => {
        console.log(idArray, "idArray value in sync");
        console.log(items);
        return (
            items.map((item) =>
                <tr key={item.id} className={item.selected ? "selected" : ""}>
                    <th scope="row">
                  <input
                      key={item.id}
                      type="checkbox"
                      name={item.id}
                      checked={listData.length ? item.isChecked : checkStatusResultStarted(item)?false:allChecked}
                      onChange={handleChange}
                  />
              </th>
              <td>
              <p> {item.email} </p>
              </td>
                    <td>
                        <p> {item.mail_server} </p>
                    </td>

                    <td>
                        {
                            createToggleButton(item)
                        }
                    </td>
                    <td>
                        {
                            syncStatu(item)
                        }
                    </td>
                </tr>
            )
        )
    }

    return (


        <div className="m-4">
        <h3 className="title">List of entries</h3>
        <div className="table-responsive">
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>
                            <input
                                type="checkbox"
                                name="0"
                                checked={allChecked}
                                onChange={handleChange}
                            />
                        </th>
                        <th scope="col">Email ID</th>
                        <th scope="col">Email Server</th>
                        <th scope="col">Disable/Enable</th>
                        <th scope="col">Status</th>
                    </tr>
                </thead>
                <tbody>
                    {renderTableData(userData, idArray)}                    
                </tbody>

            </table>

        </div>
    </div>



      );

}
