                "homeTeamScore": 3,
                "awayTeamScore": 0
            },
            {
                "matchDate": 1651744228685,
                "stadium": "Maracanã",
                "homeTeam": "Brazil",
                "awayTeam": "Cameroon",
                "matchPlayed": true,
                "homeTeamScore": 4,
                "awayTeamScore": 4
            },
            {
                "matchDate": 1651744228685,
                "stadium": "Stade de Suisse",
                "homeTeam": "Switzerland",
                "awayTeam": "Cameroon",
                "matchPlayed": true,
                "homeTeamScore": 2,
                "awayTeamScore": 2
            }
        ];

        let teams = [
            {
                 T : "Brazil" ,
                 MP: 0,
