        }

            const renderTableData = (items) => {

                console.log(items);
                return (
                    items.map((item) =>
                        <tr key={item.id} className={item.selected ? "selected" : ""}>

                            <td>

                                {item.homeTeam === "Brazil" && <img src="https://countryflagsapi.com/png/br" alt="Brazil flag" width="53px" height="37px" />}
                                {item.homeTeam === "Serbia" &&  <img src="https://countryflagsapi.com/png/rs" alt="Serbia flag" width="53px" height="37px"/>}
                                {item.homeTeam === "Switzerland" &&  <img src="https://countryflagsapi.com/png/ch" alt="Switzerland flag" width="53px" height="37px"  />}
                                {item.homeTeam === "Cameroon" && <img src="https://countryflagsapi.com/png/cm" alt="Cameroon flag" />}
                                <p> {item.homeTeam} </p>
                             </td>


                            <td>
                                {   item.MP    }
                            </td>
                            <td>
                            {   item.GF    }
                            </td>
                            <td>
                            {   item.GA    }
                            </td>
                            <td>
                            {   item.GD    }
                            </td>
                            <td>
                            {   item.Points   }
                            </td>
                        </tr>
                    )
                )
            }

            return (
                <div className="m-4">
                <h3 className="title">League Standings</h3>
                <div className="table-responsive">
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                <th scope="col">Team Name</th>
                                <th scope="col">MP</th>
                                <th scope="col">GF</th>
                                <th scope="col">GA</th>
                                <th scope="col">Points</th>
                            </tr>
                        </thead>
                        <tbody>
                            {renderTableData(matches)}                    
                        </tbody>

                    </table>

                </div>
            </div>

              );
}
